import React, { useState, useEffect } from 'react';
import Card from '../UI/Card';
import styles from './AvailableFood.module.css';
import FoodItem from './FoodItem/FoodItem';

const AvailableFood = () => {
  const [foods, setFoods] = useState([]);

  useEffect(() => {
    fetchFoodHandler();
  }, []);

  async function fetchFoodHandler() {
    const res = await fetch(
      'https://react-practice-9b279-default-rtdb.firebaseio.com/foods.json'
    );
    const data = await res.json();

    const transformedFoods = data.results.map((foodData) => {
      return {
        id: foodData.id,
        name: foodData.name,
        desc: foodData.desc,
        price: foodData.price,
      };
    });
    setFoods(transformedFoods);
  }

  return (
    <section className={styles.foods}>
      <Card>
        <ul>
          {foods.map((food) => (
            <FoodItem
              id={food.id}
              key={food.id}
              name={food.name}
              desc={food.desc}
              price={food.price}
            />
          ))}
        </ul>
      </Card>
    </section>
  );
};

export default AvailableFood;
